mapper.update:
update PRODUCT set PD_NAME = #{pd_name, jdbcType = VARCHAR }, CTG_CODE = #{ctg_code, jdbcType = VARCHAR},
                   PD_PRICE = #{pd_price, jdbcType = NUMERIC}, PD_STOCK = #{pd_stock, jdbcType = NUMERIC},
                   PD_CONTENT = #{pd_content, jdbcType = VARCHAR}
where PD_NUM = ${pd_num}
